                //GC.Collect(2 GCCollectionMode.Forced true);
                // GC.WaitForPendingFinalizers();
                //GC.Collect(2 GCCollectionMode.Forced true);
                //GC.WaitForPendingFinalizers();
            });
            await t1;
        }
    });

    var cnt = 0L;

    // if we put it here everything works as expected
    // ICursor<int int> cursor;

    var readTask = Task.Run(async () =>
    {
