    L001d: mov rax rcx
    L0020: mov rcx rax
    L0023: mov rax rcx
    L0026: mov rcx rax
    L0029: lea rax [rsp+0x20]
    L002e: mov [rax] rcx
    L0031: mov rcx [rsp+0x20]
    L0036: cmp [rcx] ecx
    L0038: call C.M()
    L003d: nop
    L003e: add rsp 0x28
    L0042: ret

