    public float f2;
    public IntPtr f3;
}

class Test
{
    [MethodImpl(MethodImplOptions.NoInlining)]
    static void foo(SequentialStruct s)
    {
        Console.WriteLine(s.f1);
        Console.WriteLine(s.f2);
    }

    static void Main()
    {
            SequentialStruct ss = new SequentialStruct();
            ss.f0 = 100;
            ss.f1 = 1;
            ss.f2 = 10.0f;
            ss.f3 = new IntPtr(42);
            foo(ss);
    }
}
