IssueDetail.Description:
VS 15.7.3 .NET Framework 4.7.2

```csharp
namespace ConsoleApp3
{
	class Program
	{
		static void Main(string[] args)
		{
			var summary = BenchmarkRunner.Run<Test>();
		}
	}

	[DisassemblyDiagnoser(printAsm: true printSource: true printPrologAndEpilog: false recursiveDepth: 2)]
	public class Test
	{
		[Benchmark]
		public unsafe ulong Run1()
		{
			var length = 1024;
			ulong res = 0;
			var data = stackalloc byte[length];
			var pdata = new Ptr1<byte>(data);
			for (int i = 0; i < 10000; i++)
			{
				res += (ulong)Method1(ref pdata length);
			}
			return res;
		}

		[Benchmark]
		public unsafe ulong Run2()
		{
			var length = 1024;
			ulong res = 0;
			var data = stackalloc byte[length];
			var pdata = new Ptr2<byte>(data);
			for (int i = 0; i < 10000; i++)
			{
				res += (ulong)Method2(ref pdata length);
			}
			return res;
		}

		[MethodImpl(MethodImplOptions.NoInlining)]
		public int Method1(ref Ptr1<byte> data int length)
		{
			var i = 0;
			var res = 0;
			do
			{
				data.Ref = (byte)(data.Ref + data[i++]);
				res += data.Ref;
			} while (--length != 0);
			return res;
		}

		[MethodImpl(MethodImplOptions.NoInlining)]
		public int Method2(ref Ptr2<byte> data int length)
		{
			var i = 0;
			var res = 0;
			do
			{
				data.Ref = (byte)(data.Ref + data[i++]);
				res += data.Ref;
			} while (--length != 0);
			return res;
		}
	}

	public unsafe struct Ptr1<T> where T : unmanaged
	{
		private readonly T* _nptr;

		public Ptr1(void* nptr) => _nptr = (T*)nptr;

		public ref T Ref
		{
			[MethodImpl(MethodImplOptions.AggressiveInlining)]
			get { return ref *(_nptr); }
		}

		public ref T this[int offset]
		{
			[MethodImpl(MethodImplOptions.AggressiveInlining)]
			get { return ref *(_nptr + offset); }
		}
	}

	public unsafe struct Ptr2<T> where T : unmanaged
	{
		private static readonly int TSize = Unsafe.SizeOf<T>();
		private readonly byte* _nptr;

		public Ptr2(void* nptr) => _nptr = (byte*) nptr;

		public ref T Ref
		{
			[MethodImpl(MethodImplOptions.AggressiveInlining)]
			get {return ref Unsafe.AsRef<T>(_nptr); }
		}

		public ref T this[int offset] 
		{
			[MethodImpl(MethodImplOptions.AggressiveInlining)]
			get { return ref Unsafe.AsRef<T>(_nptr + offset * TSize); }
		}
	}
}
```

