IssueDetail.Title_Description:
Methods with operations on pointers to unmanaged type (T*, where T: unmanaged) not getting inlined VS 15.7.3 .NET Framework 4.7.2

```csharp
namespace ConsoleApp3
{
	class Program
	{
		static void Main(string[] args)
		{
			var summary = BenchmarkRunner.Run<Test>();
		}
	}

	[DisassemblyDiagnoser(printAsm: true printSource: true printPrologAndEpilog: false recursiveDepth: 2)]
	public class Test
	{
		[Benchmark]
		public unsafe ulong Run1()
		{
			var length = 1024;
			ulong res = 0;
			var data = stackalloc byte[length];
			var pdata = new Ptr1<byte>(data);
			for (int i = 0; i < 10000; i++)
			{
				res += (ulong)Method1(ref pdata length);
			}
			return res;
		}

		[Benchmark]
		public unsafe ulong Run2()
		{
			var length = 1024;
			ulong res = 0;
			var data = stackalloc byte[length];
			var pdata = new Ptr2<byte>(data);
			for (int i = 0; i < 10000; i++)
			{
				res += (ulong)Method2(ref pdata length);
			}
			return res;
		}

		[MethodImpl(MethodImplOptions.NoInlining)]
		public int Method1(ref Ptr1<byte> data int length)
		{
			var i = 0;
			var res = 0;
			do
			{
				data.Ref = (byte)(data.Ref + data[i++]);
				res += data.Ref;
			} while (--length != 0);
			return res;
		}

		[MethodImpl(MethodImplOptions.NoInlining)]
		public int Method2(ref Ptr2<byte> data int length)
		{
			var i = 0;
			var res = 0;
			do
			{
				data.Ref = (byte)(data.Ref + data[i++]);
				res += data.Ref;
			} while (--length != 0);
			return res;
		}
	}

	public unsafe struct Ptr1<T> where T : unmanaged
	{
		private readonly T* _nptr;

		public Ptr1(void* nptr) => _nptr = (T*)nptr;

		public ref T Ref
		{
