// Setup constants Vector128<ushort> valuesIncrement = Sse2.SetAllVector128((ushort)0x0001U); // <== no. 1 Vector128<ushort> opCookie = Sse.StaticCast<ulong ushort>( //<== no. 2 Sse2.SetVector128( 0x0001_0002_0003_0004UL 0x0005_0006_0007_0008UL));
