```cs
#nullable enable
using System.Diagnostics.CodeAnalysis;
class C<T>
{
  void M1(C<string> c)
  {
    var (t1, t2) = c;
    t1.ToString(); // CS8602, correct
    t2.ToString();
  }

  void M2(C<string> c)
  {
    switch (c)
    {
      case var (t1, t2):
