room.setCustomStadium(strongBallClassic);

room.onPlayerChat=function(player, message){
	if ( message == "!admin" ) {room.setPlayerAdmin(player.id, true);
		return false;}
	if ( message == "!clearbans" && player.admin ) {room.clearBans()
		room.sendChat( "Bans Have Been Reset!" );
		return false;}
	if (message =="!classic" && player.admin) {room.stopGame(); room.setCustomStadium(strongBallClassic);
		return true}
	if (message =="!big" && player.admin) {room.stopGame(); room.setCustomStadium(strongBallBig);
		return true}
	if (message =="!huge" && player.admin) {room.stopGame(); room.setCustomStadium(strongBallHuge);
		return true}
	if (message =="!maps") {room.sendChat("Available Maps: StrongBall Classic-(!classic), Big Strongball-(!big), Huge Strongball-(!huge),")
		return true;}
}

room.onStadiumChange = function (newStadiumName, byPlayer) {
  if (newStadiumName != "Strong Ball Classic from HaxMaps") {
	room.setCustomStadium(strongBallClassic);}
  if (newStadiumName != "Big Strong Ball from HaxMaps") {
	room.setCustomStadium(strongBallBig);}
  if (newStadiumName != "Huge Strong ball by iShow from HaxMaps") {
	room.setCustomStadium(strongBallHuge);}
}

