    public readonly void M() {
        var t = this[..]; // missing CS8656
    }
}

namespace System {
  public struct Index {
      public Index(int index, bool fromEnd) { }
      public int GetOffset(int length) => 0;
  }

  public struct Range {
      public Range(Index start, Index end) { }
