await socket.ConnectAsync( remoteEndPoint )
			.ConfigureAwait( false );

var networkStream = new NetworkStream( socket, true );
var sslStream = new SslStream( 
	networkStream,
	false,
	( sender, certificate, chain, sslPolicyErrors ) => true, // Accept invalid cert...testing
	( sender, targetHost, localCertificates, remoteCertificate, acceptableIssuers ) =>
	{
	   if ( localCertificates.Count == 1 )
		   return localCertificates[0];

	   if ( localCertificates.Count <= 1 )
		   return null;

	   const String subject = "CertSubject";
	   var match = localCertificates.Cast<X509Certificate2>()
									.FirstOrDefault( certificate => String.Compare( certificate.Subject, subject, StringComparison.OrdinalIgnoreCase ) == 0 );
	   return match;
	} );

using var store = new X509Store( "My", StoreLocation.LocalMachine );
store.Open( OpenFlags.ReadOnly );
var collection = store.Certificates;

await sslStream.AuthenticateAsClientAsync( hostName, collection, SslProtocols.Tls | SslProtocols.Tls11 | SslProtocols.Tls12 | SslProtocols.Tls13, false )
                .ConfigureAwait( false );
