```C# using BenchmarkDotNet.Attributes; using BenchmarkDotNet.Diagnosers; using BenchmarkDotNet.Running; using System; using System.Linq; using System.Text;
[MemoryDiagnoser] public class Program { static void Main(string[] args) => BenchmarkSwitcher.FromTypes(new[] { typeof(Program) }).Run(args);
[Params(10, 100, 100_000)] public int Length { get; set; }
[Params(InputKind.Unreserved, InputKind.Unicode, InputKind.OneReservedThenUnreserved, InputKind.Alternating)] public InputKind Kind { get; set; }
public enum InputKind { Unreserved, Unicode, OneReservedThenUnreserved, Alternating }
[GlobalSetup] public void Setup() { switch (Kind) { case InputKind.Unreserved: _input = new string('s', Length); break; case InputKind.Unicode: _input = string.Concat(Enumerable.Repeat("\xD83D\xDE00", Length)); break; case InputKind.OneReservedThenUnreserved: _input = "<" + new string('s', Length - 1); break; case InputKind.Alternating: var sb = new StringBuilder(Length); for (int i = 0; i < Length; i++) { switch (i % 3) { case 0: sb.Append('s'); break; case 1: sb.Append('<'); break; default: sb.Append("\xD83D\xDE00"); break; } } _input = sb.ToString(); break; } }
private string _input;
[Benchmark] public string EscapeData() => Uri.EscapeDataString(_input); [Benchmark] public string EscapeUri() => Uri.EscapeUriString(_input); } ```
