            var obj1 = System.Text.Json.JsonSerializer.Deserialize<Bar>("{ \"Value\": 1.23 }"); // throws an exception

            var obj2 = new Bar { Value = 1.23, };
            var text = System.Text.Json.JsonSerializer.Serialize(obj2); // throws an exception as well
        }
