    {
        Console.WriteLine("Catch 1");
    }
    catch (FileNotFoundException)
    {
        Console.WriteLine("Catch 2");
    }
}
catch (Exception)
{
    Console.WriteLine("Catch 3");
}
Console.WriteLine("End");
```

