var loginResult = await client.PostAsync ("/login", null);

void test2(void** ptr)
{
    printf("current vall: %p\n", *ptr);
    *ptr = (void*)0xDEADBEEF;
}
unsafe class Program
{
    [DllImport("sht", CallingConvention = CallingConvention.Cdecl)]
    public static extern void test2(void** ptr);

    // same pinvoke but with SafeHandle
    [DllImport("sht", CallingConvention = CallingConvention.Cdecl, EntryPoint = "test2")]
    public static extern void test2_sh(ref MySafeHandle ptr);

    static void Main(string[] args)
    {
        // raw pointers work:
        long ptr;
        test2((void**)&ptr); // prints (nil)
        test2((void**)&ptr); // prints (0xdeadbeef)

        // safehandle doesn't
        MySafeHandle handle = new MySafeHandle();
        test2_sh(ref handle); // prints (nil)
        test2_sh(ref handle); // prints (nil) -- but should be 0xdeadbeef!
    }
}

internal sealed class MySafeHandle : SafeHandle
{
    public MySafeHandle() : base(IntPtr.Zero, true) {}

    public override bool IsInvalid => handle == IntPtr.Zero;

    protected override bool ReleaseHandle()
    {
        Console.WriteLine("ReleaseHandle()");
        SetHandle(IntPtr.Zero);
        return true;
    }
}
