public class MyGrain : Grain, ISomeGrain 
{
  protected override Task<object> Invoke(InvokeMethodRequest request, IGrainMethodInvoker invoker)
  {
        base.TelemetryManager.TrackTrace("Hey just pushed a trace!");

        //Terrible sample on how measure request time but you guys got the idea :P
        var now = DateTime.UtcNow;
        await base.Invoke(request, invoker);
        var elapsedTime = DateTime.UtcNow.Subtract(now).TotalMiliseconds;
        base.TelemetryManager.TrackMetric("elapsedTime", elapsedTime);
        return Task.Done;
  }

  public Task MyGrainMethod()
  {
        base.TelemetryManager.TrackEvent("MethodCalled");
  }
}

