			class C
			{
				static void Main()
				{
					if (TrySomething() is ValueTuple<string, bool> v && v.Item2 == true)
					{
						System.Console.Write(v.Item1 == null);
					}
				}
				static (string Value, bool Success) TrySomething()
				{
					return (null, true);
				}
			}

    static void Main()
    {
        {
            ValueTuple<string, bool> v = TrySomething();
            if (v.Item2 == true)
            {
                System.Console.Write(v.Item1 == null);
            }
        }
    }

    private static void Main()
    {
        ValueTuple<string, bool>? valueTuple = (object)C.TrySomething() as ValueTuple<string, bool>?;
        ValueTuple<string, bool> valueOrDefault = valueTuple.GetValueOrDefault();
        bool flag = valueTuple.HasValue && valueOrDefault.Item2;
        if (flag)
        {
            Console.Write(valueOrDefault.Item1 == null);
        }
    }


