		DumpArgs(new ArgIterator(__arglist));
	}

	static void DumpArgs(ArgIterator args)
	{
		while(args.GetRemainingCount() > 0)
		{
			TypedReference tr = args.GetNextArg();
			object arg = TypedReference.ToObject(tr);
			Console.Write(arg);
		}
	}
